show_bold() {
  if [ $1 == "CHECK" ]; then
    echo -e "\e[1m$2\e[0m $3"
  elif [ $1 == "INFO" ]; then
    echo -e "\e[94m$2\e[0m $3"
  elif [ $1 == "ERROR" ]; then
    echo -e "\e[31m$2\e[0m $3"
  fi
}

